import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
const initialValues = {
  email: '',
  username: '',
  password: ''
};

const validationSchema = Yup.object({
 
  username: Yup.string().required('Required'),
  email: Yup.string().email('Invalid email format').required('Required'),
  password: Yup.string().required('Required')
});

class CodeEvolutionForm extends Component {
  onSubmit = values => {
    console.log('Form data', values);
    axios.post('http://localhost:8000/register', { username: values.username, emai: values.email, password:values.password })
			.then(response => {
				console.log(response)
			})
			.catch(error => {
				console.log(error)
			})
  };

  render() {
    return (
      <Formik className="auth-form-container"
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={this.onSubmit}
      >
        <Form >

          <div className="form-control">
            <label htmlFor="username">username</label>
            <Field type="username" id="username" name="username" />
            <ErrorMessage name="username" />
          </div>
          <div className="form-control">
            <label htmlFor="email">E-mail</label>
            <Field type="email" id="email" name="email" />
            <ErrorMessage name="email" />
          </div>
          <div className="form-control">
            <label htmlFor="password">password</label>
            <Field type="password" id="password" name="password" />
            <ErrorMessage name="password" />
          </div>
          <button type="submit">Submit</button>
        </Form>
      </Formik>
    );
  }
}

export default CodeEvolutionForm;
